import React, { Component } from "react";

export default class Basket extends Component {
  render() {
    const { cartItems } = this.props;
    const inCart = this.props.cartItems.map(item => {
      var final = item.title;
      final = final + " X" + item.count;
      return (
        <li>
          {final}{" "}
          <button
            className="btn btn-warning text-body"
            onClick={this.handleRemoveCart(item)}
          >
            X
          </button>
        </li>
      );
    });
    return (
      <div className="alert alert-info">
        {cartItems.length === 0 ? (
          "Basket is Empty"
        ) : (
          <div>You have {cartItems.length} products in basket</div>
        )}
        <ul>{inCart}</ul>
      </div>
    );
  }
}
